commits.content:
$dark-font-color: rgb(219, 223, 236);
$dark-background: rgb(46 52 64);
$dark-header-backgournd: rgb(59, 62, 81);
$dark-sideBar-background: rgb(36, 41, 51);
$active-font-color: rgb(129, 161, 193);
$font-family: monospace;
$code-fence-font-family: -apple-system, BlinkMacSystemFont, PingFang SC, Hiragino Sans GB,
  sans-serif;
// 最外层是container
@mixin theme-dark {
  color: $dark-font-color;
  background-color: $dark-background;
  font-weight: 300;
  font-size: 18px;
  input {
    color: $dark-font-color;
    background-color: $dark-sideBar-background;
    border: 1px solid $active-font-color;
  }
  & > header {
    background-color: $dark-header-backgournd;
  }
  main {
    .side-bar {
      border-right: 1px solid $dark-header-backgournd;
      background-color: $dark-sideBar-background;
      .drag-line {
        background-color: $dark-header-backgournd;
      }
      a {
        text-decoration-color: $dark-font-color;
        color: $dark-font-color;
      }
    }
    .milkdown {
      background: $dark-background;
      color: $dark-font-color;
      font-family: $font-family;
      // 反引号，行内的代码
      .editor {
        code.code-inline {
          color: $dark-font-color;
          background-color: $dark-sideBar-background;
          font-size: initial;
        }
        // 注释
        .blockquote {
          padding-left: 14px;
          border-width: 2px;
          margin: 15px 0;
          p {
            color: rgb(153, 164, 182);
          }
        }
        .code-fence {
          margin-top: 10px;
          pre code {
            color: $dark-font-color;
            // font-family:$code-fence-font-family
          }
        }
      }
    }
  }
  .search-file {
    background-color: $dark-sideBar-background;
    li.active {
      background-color: $dark-header-backgournd;
      color: $active-font-color;
    }
  }
}
