import { Ctx, Editor } from "@milkdown/core";
import { listenerCtx } from "@milkdown/plugin-listener";

export const useConfig = (ctx:Ctx) => {
    ctx.get(listenerCtx).markdownUpdated((ctx, markdown, prevMarkdown) => {
        console.log(markdown)
      })
}
