import React, { useEffect } from 'react'
import { IFileList } from 'src/preload/index.d'
import { useUpdateHeaders } from '../hooks/useUpdateHeader'

export const FileList: React.FC<{
  fileList: IFileList
  show: boolean
  setShowFileList: (show: boolean) => void
}> = ({ fileList, setShowFileList, show }) => {
  const openFile = (filePath) => {
    window.api.clickFileList(filePath)
    setShowFileList(!show)
  }
  useEffect(() => {
    useUpdateHeaders()
  }, [])
  return (
    <ul className="file-list">
      {fileList.map(({ fileName, filePath }) => {
        return (
          <li
            key={filePath}
            onClick={(e) => {
              e.stopPropagation()
              openFile(filePath)
            }}
          >
            <div></div>
            <span>{fileName}</span>
          </li>
        )
      })}
    </ul>
  )
}
