import { FileTree } from "src/common/interface"

export const mapFileList = (fileTree: FileTree) => {
    const li = document.createElement('li')
    // 隐藏打开的根文件夹
    li.setAttribute('style', `--i: ${fileTree.level}`)
    const fileName = `<span>${fileTree.fileName}</span>`
    const input = document.createElement('input')
    if (fileTree.level === 0) li.style.display = 'none'
    if (!fileTree.isDir) {
      li.innerHTML = fileName
      li.classList.add('file-item')
      li.appendChild(input)
      li.addEventListener('click',(e)=>{
        e.stopPropagation()
        li.classList.toggle('show-input')
        input.focus()
      })
      return li
    } else {
      li.innerHTML = triangleDown + fileName
      li.classList.add('dir')
      const ul = document.createElement('ul')
      ul.addEventListener('click',e=>{
        e.stopPropagation()
        ul.classList.toggle('close')
      })
      ul.appendChild(li)
      fileTree.children.forEach((file) => {
        ul.appendChild(mapFileList(file))
      })
      return ul
    }
  }
